document.addEventListener('DOMContentLoaded', () => {const toolbarButtons = document.querySelectorAll('.toolbar-btn');const sections = document.querySelectorAll('.product-row, .cta-banner-refined');const observerOptions = {root: null,rootMargin: '-20% 0px -60% 0px',threshold: 0};const observer = new IntersectionObserver((entries) => {entries.forEach(entry => {if (entry.isIntersecting) {const id = entry.target.getAttribute('id');toolbarButtons.forEach(btn => {btn.classList.remove('active');if (btn.getAttribute('href') === `#${id}`) {btn.classList.add('active');btn.scrollIntoView({behavior: 'smooth',block: 'nearest',inline: 'center'});}});}});}, observerOptions);sections.forEach(section => {if (section.id) {observer.observe(section);}});toolbarButtons.forEach(btn => {btn.addEventListener('click', (e) => {toolbarButtons.forEach(b => b.classList.remove('active'));btn.classList.add('active');});});});