const hamburger = document.querySelector('.hamburger');const navLinks = document.querySelector('.nav-links');if (hamburger) {hamburger.addEventListener('click', (e) => {e.stopPropagation();navLinks.classList.toggle('active');hamburger.classList.toggle('active');});document.addEventListener('click', (e) => {if (navLinks.classList.contains('active') && !navLinks.contains(e.target) && !hamburger.contains(e.target)) {navLinks.classList.remove('active');hamburger.classList.remove('active');}});navLinks.querySelectorAll('a').forEach(link => {link.addEventListener('click', () => {navLinks.classList.remove('active');hamburger.classList.remove('active');});});}const slides = document.querySelectorAll('.hero-slide');const dots = document.querySelectorAll('.dot');let currentSlide = 0;const intervalTime = 5000;let slideInterval;const nextSlide = () => {slides[currentSlide].classList.remove('active');dots[currentSlide].classList.remove('active');currentSlide = (currentSlide + 1) % slides.length;slides[currentSlide].classList.add('active');dots[currentSlide].classList.add('active');};slideInterval = setInterval(nextSlide, intervalTime);dots.forEach((dot, index) => {dot.addEventListener('click', () => {clearInterval(slideInterval);slides[currentSlide].classList.remove('active');dots[currentSlide].classList.remove('active');currentSlide = index;slides[currentSlide].classList.add('active');dots[currentSlide].classList.add('active');slideInterval = setInterval(nextSlide, intervalTime);});});document.addEventListener('DOMContentLoaded', () => {document.querySelectorAll('a').forEach(link => {link.addEventListener('click', (e) => {const href = link.getAttribute('href');if (href && !href.startsWith('#') && !href.startsWith('mailto:') && !href.startsWith('tel:') && link.target !== '_blank') {e.preventDefault();document.body.classList.add('page-exit');setTimeout(() => {window.location.href = href;}, 300);}});});});const observerOptions = {threshold: 0.1};const observer = new IntersectionObserver((entries) => {entries.forEach(entry => {if (entry.isIntersecting) {entry.target.classList.add('visible');observer.unobserve(entry.target);}});}, observerOptions);document.querySelectorAll('.section-title, .product-card, .contact-card, .about-text, .about-image').forEach(el => {el.classList.add('animate-on-scroll');observer.observe(el);});const aboutCarousel = document.querySelector('.about-carousel');if (aboutCarousel) {const aboutSlides = aboutCarousel.querySelectorAll('.about-slide');let aboutCurrentSlide = 0;const aboutIntervalTime = 3000;let aboutSlideInterval;const nextAboutSlide = () => {aboutSlides[aboutCurrentSlide].classList.remove('active');aboutCurrentSlide = (aboutCurrentSlide + 1) % aboutSlides.length;aboutSlides[aboutCurrentSlide].classList.add('active');};const startAboutSlide = () => {aboutSlideInterval = setInterval(nextAboutSlide, aboutIntervalTime);};const stopAboutSlide = () => {clearInterval(aboutSlideInterval);};startAboutSlide();aboutCarousel.addEventListener('mouseenter', stopAboutSlide);aboutCarousel.addEventListener('mouseleave', startAboutSlide);}const tabBtns = document.querySelectorAll('.tab-btn');const tabPanes = document.querySelectorAll('.tab-pane');if (tabBtns.length > 0) {tabBtns.forEach(btn => {btn.addEventListener('click', () => {tabBtns.forEach(b => b.classList.remove('active'));tabPanes.forEach(p => p.classList.remove('active'));btn.classList.add('active');const targetId = btn.getAttribute('data-tab');document.getElementById(targetId).classList.add('active');});});}